75.query:
// This query retrieves a timeseries of all spans that have passed through the selected OpenTelemetry Collector instance.
timeseries {accepted=sum(otelcol_receiver_accepted_spans)}, 
filter: { in(service.name, $CollectorServiceNames) and k8s.pod.name==$K8sPodName },
by: {receiver}
| fieldsAdd type="receiver"
| append [
  timeseries {refused=sum(otelcol_receiver_refused_spans)}, 
  filter: { in(service.name, $CollectorServiceNames) and k8s.pod.name==$K8sPodName },
  by: {receiver}
  | fieldsAdd type="receiver"
] | append [
  timeseries {filtered=sum(otelcol_processor_filter_spans.filtered)},
  filter: { in(service.name, $CollectorServiceNames) and k8s.pod.name==$K8sPodName },
  by: {filter}
  | fieldsAdd type="processor"
] | append [
  timeseries {sent=sum(otelcol_exporter_sent_spans)}, 
  filter: { in(service.name, $CollectorServiceNames) and k8s.pod.name==$K8sPodName },
  by: {exporter}
  | fieldsAdd type="exporter"
] | append [
  timeseries {failed=sum(otelcol_exporter_send_failed_spans)}, 
  filter: { in(service.name, $CollectorServiceNames) and k8s.pod.name==$K8sPodName },
  by: { exporter }
  | fieldsAdd type="exporter"
]
  // todo name for filter column
| fields timeframe, interval, type, accepted, refused, sent, failed, filtered, receiver, exporter

